"use strict";(self.webpackChunkwinterblanket_archive=self.webpackChunkwinterblanket_archive||[]).push([[1477],{10:t=>{t.exports=JSON.parse('{"blogPosts":[{"id":"create-personal-website-with-docusaurus","metadata":{"permalink":"/blog/create-personal-website-with-docusaurus","source":"@site/blog/2023-06-26-create-personal-website-with-docusaurus/2023-06-26-create-personal-website-with-docusaurus.mdx","title":"\uc6c5\ub369\uc774 \ud30c\ub294 \ub370 \ud3ec\ud06c\ub808\uc778 \uc368\ubcf4\uae30","description":"Docusaurus\ub85c \uac1c\uc778 \ud648\ud398\uc774\uc9c0 \ub9cc\ub4e4\uae30\uc758 A to Z","date":"2023-06-26T00:00:00.000Z","formattedDate":"2023\ub144 6\uc6d4 26\uc77c","tags":[{"label":"\ud504\ub85c\uadf8\ub798\ubc0d \uacf5\ubd80 \uae30\ub85d","permalink":"/blog/tags/\ud504\ub85c\uadf8\ub798\ubc0d-\uacf5\ubd80-\uae30\ub85d"},{"label":"Figma","permalink":"/blog/tags/figma"},{"label":"Design System","permalink":"/blog/tags/design-system"},{"label":"React","permalink":"/blog/tags/react"},{"label":"Docusaurus","permalink":"/blog/tags/docusaurus"},{"label":"Github Pages","permalink":"/blog/tags/github-pages"}],"readingTime":16.435,"hasTruncateMarker":false,"authors":[],"frontMatter":{"title":"\uc6c5\ub369\uc774 \ud30c\ub294 \ub370 \ud3ec\ud06c\ub808\uc778 \uc368\ubcf4\uae30","description":"Docusaurus\ub85c \uac1c\uc778 \ud648\ud398\uc774\uc9c0 \ub9cc\ub4e4\uae30\uc758 A to Z","date":"2023-06-26","draft":false,"tags":["\ud504\ub85c\uadf8\ub798\ubc0d \uacf5\ubd80 \uae30\ub85d","Figma","Design System","React","Docusaurus","Github Pages"],"keywords":["\ud504\ub85c\uadf8\ub798\ubc0d \uacf5\ubd80 \uae30\ub85d","Figma","Design System","React","Docusaurus","Github Pages"],"image":"./create-personal-website-with-docusaurus-thumbnail.png","slug":"create-personal-website-with-docusaurus"},"nextItem":{"title":"\ub9e4\uc9c1 \ub9c8\uc6b0\uc2a4, \ud2b8\ub799\ud328\ub4dc \uc5c6\uc774 \ub9e5 \uc81c\uc2a4\ucc98 \ud65c\uc6a9\ud558\uae30","permalink":"/blog/mac-gestures-without-trackpad"}},"content":"## Docusaurus\ub85c \uac1c\uc778 \ud648\ud398\uc774\uc9c0 \ub9cc\ub4e4\uae30\\n\\n\ube14\ub85c\uadf8\ub97c \uacb8\ud558\ub294 \uac1c\uc778 \ud648\ud398\uc774\uc9c0\ub97c \ub9cc\ub4e4\uace0 \uc2f6\ub2e4\ub294 \uc0dd\uac01\uc744 \uaf64 \uc624\ub798\uc804\ubd80\ud130 \ud574\uc654\ub2e4. \uc2dc\ub3c4\ud588\ub358 \uc801\ub3c4 \ub9ce\uace0 \ub2e4\uc591\ud55c \ud234\uc744 \uc0ac\uc6a9\ud588\ub2e4. \ub9cc\ub4dc\ub294 \uac8c \uc5b4\ub824\uc6cc\uc11c \ud3ec\uae30\ud55c \uc801\ub3c4 \uc788\uace0, \ube14\ub85c\uadf8\ub77c\uace0 \ud560 \ub9cc\ud07c \uafb8\uc900\ud788 \uae00 \uc4f0\ub294 \uac78 \uc2e4\ud328\ud574\uc11c \ud3ec\uae30\ud55c \uc801\ub3c4 \ub9ce\uc558\ub2e4. \uc6cc\ub4dc\ud504\ub808\uc2a4, \ub178\uc158, Docusaurus \ub4f1 \ub2e4\uc591\ud55c \ub3c4\uad6c\ub97c \uc368\ubd24\ub294\ub370 Docusaurus\ub294 \uadf8 \uc911 \ub2e8\uc5f0 \ub09c\uc774\ub3c4\uac00 \ub192\uc558\ub358 \ubc29\uc2dd\uc774\ub2e4. React\uc5d0 \ub300\ud55c \uc774\ud574\uac00 \ud544\uc694\ud558\uace0 \ub2f9\uc5f0\ud788 \uae30\ucd08\uc801\uc778 \ud504\ub85c\uadf8\ub798\ubc0d\uc774 \uac00\ub2a5\ud574\uc57c \ud55c\ub2e4. \ub2e4\ub9cc \ud504\ub85c\uadf8\ub798\ubc0d \ub09c\uc774\ub3c4\uac00 \ub192\ub2e4\ub294 \uac74 \ub2f9\uc5f0\ud788 \uadf8\ub9cc\ud07c \uc790\uc720\ub3c4\uac00 \ub192\ub2e4\ub294 \ub73b\uc774\uae30 \ub54c\ubb38\uc5d0 \uac00\uc7a5 \ub04c\ub838\ub358 \ubc29\uc2dd\uc774\uae30\ub3c4 \ud588\ub2e4. \\n\\n---\\n\\n## \uac1c\uc778 \ud648\ud398\uc774\uc9c0\ub97c \ub9cc\ub4dc\ub294 \ub2e4\uc591\ud55c \ub3c4\uad6c\ub4e4\\n\\n### \uc6cc\ub4dc\ud504\ub808\uc2a4 \\n\\n<full-image>![\uc6cc\ub4dc\ud504\ub808\uc2a4 \ud648\ud398\uc774\uc9c0 \uba54\uc778 \ud654\uba74](./homepage_wordpress.png)</full-image>\\n<full-caption>\uc6cc\ub4dc\ud504\ub808\uc2a4 \ud648\ud398\uc774\uc9c0</full-caption>\\n\\n\ud55c 10\ub144 \uc804\uc5d0\ub294 \ub178\ucf54\ub4dc \ud648\ud398\uc774\uc9c0 \uc81c\uc791\uc740 \uace7 [\uc6cc\ub4dc\ud504\ub808\uc2a4](https://wordpress.com/ko/)\uc600\uc9c0\ub9cc, \uc694\uc998\uc740 Wix \ub4f1 \ub354 \uc0ac\uc6a9\uc790 \uce5c\ud654\uc801\uc778 \ub3c4\uad6c\ub4e4\uc774 \ub9ce\uc774 \ub098\uc640\uc11c \uc6cc\ub4dc\ud504\ub808\uc2a4\ub97c \uc4f0\ub294 \uacbd\uc6b0\ub294 \uc798 \ubcf4\uc9c0 \ubabb\ud558\ub294 \uac83 \uac19\ub2e4. \ud559\uc0dd \uc2dc\uc808\uc5d0 \ud648\ud398\uc774\uc9c0 \uc81c\uc791 \uc678\uc8fc\ub97c \ud558\uba74\uc11c \uaf64 \uae4a\uac8c \ub2e4\ub904\ubd24\ub294\ub370 \uad00\ub9ac\uc6a9 \ud398\uc774\uc9c0\uac00 \uc798\ub418\uc5b4 \uc788\uc5b4\uc11c \ud648\ud398\uc774\uc9c0 \uc678\uc8fc\uc6a9\uc73c\ub85c\ub294 \uc81c\uaca9\uc774\uc5c8\ub2e4. \ub2e4\ub9cc \ub514\uc790\uc778\uc744 \uae54\ub054\ud558\uac8c \ub9cc\ub4e4\uae30\uc5d0\ub294 CSS\uac00 \ubb18\ud558\uac8c \uae68\uc9c0\ub294 \uac10\uc774 \uc788\uc5c8\uace0, PHP \uae30\ubc18\uc774\uc5b4\uc11c HTML, CSS \uc815\ub3c4\ub9cc \uc544\ub294 \uc0c1\ud0dc\uc5d0\uc11c \ucee4\uc2a4\ud130\ub9c8\uc774\uc9d5\uc5d0 \uc5ec\ub7ec \ud55c\uacc4\uac00 \uc788\uc5c8\ub2e4. \uadf8\ub7ec\ud55c \ud55c\uacc4\ub97c \ubc29\ub300\ud55c \ucee4\ubba4\ub2c8\ud2f0\uc5d0\uc11c \ucc3e\uc744 \uc218 \uc788\ub294 \ud50c\ub7ec\uadf8\uc778\uc73c\ub85c \ud574\uacb0\ud558\uace4 \ud558\uc9c0\ub9cc, \ub2f9\uc5f0\ud788 \ud50c\ub7ec\uadf8\uc778\ub9c8\ub2e4 \ub514\uc790\uc778 \uc2a4\ud0c0\uc77c\uc774 \ub2e4\ub974\ub2e4. \uc6cc\ub4dc\ud504\ub808\uc2a4\ub294 \uc0ac\uc6a9\ud588\ub358 \ub3c4\uad6c \uc911 \ub514\uc790\uc778 \uce21\uba74\uc5d0\uc11c \ud004\ub9ac\ud2f0\ub97c \ub192\uc774\uae30\uac00 \uac00\uc7a5 \uc5b4\ub824\uc6b4 \ud3b8\uc774\uc5c8\ub2e4. \ub2e4\ub9cc \ub0b4\uac00 \uacaa\uc5c8\ub358 \uc6cc\ub4dc\ud504\ub808\uc2a4\ub294 \uac70\uc758 10\ub144 \uc804 \uae30\uc900\uc774\ub2c8 \ud604\uc7ac\ub294 \ub2f9\uc2dc \uacaa\uc5c8\ub358 \ubb38\uc81c\uac00 \ub9ce\uc774 \uac1c\uc120\ub410\uc744 \uac70\ub77c \uc0dd\uac01\ud55c\ub2e4. \\n\\n\uc7a5\uc810\\n- \uc124\uce58\uc640 \ud568\uaed8 \uc54c\uc544\uc11c \uc0dd\uc131\ub418\ub294 \uad00\ub9ac\uc6a9 \ud398\uc774\uc9c0\\n- \ubc29\ub300\ud55c \uc368\ub4dc\ud30c\ud2f0 \ucee4\ubba4\ub2c8\ud2f0\\n\\n\ub2e8\uc810\\n- \ub514\uc790\uc778 \uc644\uc131\ub3c4\ub97c \ub04c\uc5b4\uc62c\ub9ac\uae30 \uc5b4\ub824\uc6c0\\n- \uad00\ub828 \uc790\ub8cc\ub098 \ucee4\ubba4\ub2c8\ud2f0\uac00 \ub300\ubd80\ubd84 \uc601\uc5b4 \uae30\ubc18\\n\\n### \ub178\uc158\\n\\n<full-image>![\ub178\uc158 \ud648\ud398\uc774\uc9c0 \uba54\uc778 \ud654\uba74](./homepage_notion.png)</full-image>\\n<full-caption>\ub178\uc158 \ud648\ud398\uc774\uc9c0</full-caption>\\n\\n\uc804\ucc9c\ud6c4 \ub9cc\ub2a5 \ub3c4\uad6c\ub85c \uc4f0\uc774\ub294 [\ub178\uc158](https://notion.so)\uc774\ub2e4\ubcf4\ub2c8 \uac1c\uc778 \ube14\ub85c\uadf8\ub85c \uafb8\ubbfc \uc0ac\ub840\ub3c4 \uc27d\uac8c \ucc3e\uc544\ubcfc \uc218 \uc788\ub2e4. \ub514\uc790\uc778\uc801 \uc644\uc131\ub3c4\ub294 \ub9d0\ud560 \uac83\ub3c4 \uc5c6\uace0 \ud398\uc774\uc9c0 \ud558\ub098\ud558\ub098\ub97c \uafb8\ubbf8\ub294 \uc7ac\ubbf8\ub294 \uac00\uc7a5 \uc27d\uace0 \uc790\uc720\ub86d\ub2e4. \ub178\uc158\uc73c\ub85c \uae00\uc744 \uc368\uc11c SNS\uc5d0 \uc62c\ub9b0 \uc801\ub3c4 \uc788\uc5c8\uace0 \uaf64 \ub9ce\uc740 \ub0b4\uc6a9\uc744 \uc14b\ud305\ud588\uc9c0\ub9cc \uc6f9 \uc720\uc785\uc774 \ubd88\ub9ac\ud558\ub2e4\ub294 \uc810 \ub54c\ubb38\uc5d0 \uc624\ub798 \uc4f0\uae34 \uc5b4\ub824\uc6e0\ub2e4. \uc6b0\ud53c \uac19\uc740 \ub3c4\uad6c\ub97c \uc4f0\uba74 \uad6c\uae00 \uc560\ub110\ub9ac\ud2f1\uc2a4\ub97c \ubd99\uc77c \uc218 \uc788\uc9c0\ub9cc \uc5b4\ub514\uae4c\uc9c0\ub098 \ubc29\ubb38\ud55c \uc0ac\ub78c\uc5d0 \ub300\ud55c \uc815\ubcf4\ub97c \uc54c \uc218 \uc788\uc744 \ubfd0, \ub178\uc158 \ud398\uc774\uc9c0 \uc790\uccb4\uac00 \uc54c\uc544\uc11c \uad6c\uae00 \uac80\uc0c9\uc5d0 \ub178\ucd9c\ub418\uac8c \ud560 \uc21c \uc5c6\uc5c8\ub2e4. \ub098 \uc2a4\uc2a4\ub85c\ub3c4 \uad6c\uae00\uc5d0 \uc790\ub8cc\ub97c \ucc3e\uc744 \ub54c \ub178\uc158 \ud398\uc774\uc9c0\uac00 \uac80\uc0c9\ub41c \uae30\uc5b5\uc740 \uac70\uc758 \uc5c6\uc5c8\ub2e4. \uac00\ub054\uc529 \ub178\uc158 \ud398\uc774\uc9c0\ub97c \ubcf4\uac8c \ub418\ub294 \uacbd\uc6b0\ub294 \uac80\uc0c9\ub41c \uc6f9\ud398\uc774\uc9c0\uc5d0\uc11c \ub178\uc158 \ud398\uc774\uc9c0 \ub9c1\ud06c\ub97c \uac78\uc5b4\ub193\uc544\uc11c \ud074\ub9ad\ud574\uc11c \ub118\uc5b4\uac14\uc744 \ub54c \ubfd0\uc774\uc5c8\ub2e4. \ubc18\ub4dc\uc2dc \uc218\uc775\ud654\ub97c \ud558\uace0 \uc2f6\uc5c8\ub358 \uac83\uc740 \uc544\ub2c8\uc9c0\ub9cc \uadf8\ub798\ub3c4 \uc544\ubb34\ub3c4 \ubcf4\uc9c0 \uc54a\uc744 \uae00\uc744 \uafb8\uc900\ud788 \uc4f0\ub294 \uac74 \uc5b4\ub824\uc6b8 \uac8c \ubed4\ud588\ub2e4. \ub178\uc158\uc740 \uadf8\ub7f0 \uc810\uc5d0\uc11c \uc790\uc5f0\uc2a4\ub7f0 \uc720\uc785 \uc99d\uac00\uac00 \uc560\ucd08\uc5d0 \ubd88\uac00\ub2a5\ud560 \uac83\ucc98\ub7fc \ubcf4\uc600\uae30 \ub54c\ubb38\uc5d0 \uc7a5\uae30\uc801\uc778 \uc218\ub2e8\uc73c\ub85c \uc4f0\uae30\uc5d4 \ub9de\uc9c0 \uc54a\ub2e4\uace0 \uc0dd\uac01\ud588\ub2e4. \\n\\n\uc7a5\uc810\\n- \uac00\uc7a5 \uac04\ud3b8\ud55c \uc0ac\uc6a9\uc131\ub9cc\uc73c\ub85c \uac00\uc7a5 \uc608\uc05c \ub514\uc790\uc778\uc744 \ub9cc\ub4e4 \uc218 \uc788\uc74c\\n- \ub2e4\uc591\ud55c \ub178\uc158 \ud15c\ud50c\ub9bf\uc744 \ucc38\uace0\ud560 \uc218 \uc788\uc74c\\n\\n\ub2e8\uc810\\n- \uc6f9 \uc720\uc785\uc774 \ub9e4\uc6b0 \ubd88\ub9ac\ud568\\n- \ubc29\ubb38\uc790 \ubd84\uc11d \ub4f1, \ubcf8\uaca9\uc801\uc73c\ub85c \uc6f9\ud398\uc774\uc9c0\ucc98\ub7fc \uc4f0\ub824\uba74 [oopy](https://www.oopy.io)\uac19\uc740 \uc368\ub4dc\ud30c\ud2f0 \ub3c4\uad6c\uac00 \ubc18\ub4dc\uc2dc \ud544\uc694\ud568\\n\\n### Docusaurus\\n\\n<full-image>![Docusaurus \ud648\ud398\uc774\uc9c0 \uba54\uc778 \ud654\uba74](./homepage_docusaurus.png)</full-image>\\n<full-caption>Docusaurus \ud648\ud398\uc774\uc9c0</full-caption>\\n\\n[Docusaurus](https://docusaurus.io)\ub97c \ucc98\uc74c \uc54c\uac8c \ub41c \uac74 \ud68c\uc0ac \uc5c5\ubb34 \ub54c\ubb38\uc5d0 \uc0ac\uc6a9\uc790 \uac00\uc774\ub4dc \uc6f9\ud398\uc774\uc9c0\ub97c \ub9cc\ub4e4\uc5b4\uc57c \ud588\uc744 \ub54c\uc600\ub2e4. \uc0ac\uc6a9\uc790 \uac00\uc774\ub4dc \uc81c\uc791\uc5d0 \uac1c\ubc1c \ub9ac\uc18c\uc2a4\ub97c \ud22c\uc785\ud560 \uc218 \uc5c6\ub294 \uc0c1\ud669\uc774\uc5c8\uae30 \ub54c\ubb38\uc5d0 \uae30\ubcf8\uc801\uc778 \ud504\ub85c\uadf8\ub798\ubc0d\uc774 \uac00\ub2a5\ud55c \ub0b4 \ub2a5\ub825\ub9cc\uc73c\ub85c \uc6f9\ud398\uc774\uc9c0 \uac1c\ubc1c\uc774 \uc190\uc27d\uac8c \uac00\ub2a5\ud574\uc57c \ud588\ub2e4. \ub3d9\uc2dc\uc5d0 \uac00\uc774\ub4dc \ub0b4\uc6a9 \uc790\uccb4\ub294 \uc77c\ubc18\uc801\uc778 \ubb38\uc11c \uc791\uc5c5 \ucc98\ub7fc \uc9c4\ud589\ud560 \uc218 \uc788\ub2e4\ub294 \uc810\uc774 \uc7a5\uc810\uc774\uc5c8\ub2e4. \uc0ac\uc6a9\uc790 \uac00\uc774\ub4dc \uc81c\uc791\uc6a9 \uc11c\ube44\uc2a4\ub85c\ub294 [Gitbook](https://www.gitbook.com)\ub3c4 \uc788\uace0, \uc694\uc998\uc740 \ub178\uc158\uc744 \uc8fc\ub85c \uc0ac\uc6a9\ud558\ub294 \uac83 \uac19\ub2e4. \ub2f9\uc2dc\uc5d0\ub294 \ub178\uc158\uc744 \uc798 \ubab0\ub790\uace0 Gitbook\uc740 \uc720\ub8cc \uacb0\uc81c\uac00 \uc6d4\ubcc4 \uad6c\ub3c5\uc73c\ub85c \ub4e4\uc5b4\uac00\uc57c \ud558\ub294 \uc810\uc774 \ubc88\uac70\ub85c\uc6e0\ub2e4. \ud070 \uae08\uc561\uc740 \uc544\ub2c8\uc5c8\uc9c0\ub9cc Docusaurus\ub294 \ub77c\uc774\ube0c\ub7ec\ub9ac\ub77c \ubb34\ub8cc\uc774\uace0, \ud504\ub85c\uadf8\ub798\ubc0d\uc774 \uc804\ud600 \ubd88\uac00\ub2a5\ud55c \uc0ac\ub78c\uc774 \uad00\ub9ac\ud560 \uc0c1\ud669\uc744 \uace0\ub824\ud574\uc57c \ud558\uc9c4 \uc54a\uc558\uae30 \ub54c\ubb38\uc5d0 \uacb0\uacfc\uc801\uc73c\ub85c Docusaurus\ub85c \uc81c\uc791\ud588\uc5c8\ub2e4. \\n\\nDocusaurus\ub294 \uae30\ubcf8\uc801\uc73c\ub85c \uc0ac\uc6a9\uc790 \uac00\uc774\ub4dc\uc640 \uac19\uc740 Docs \uad00\ub9ac\uc6a9 \ub77c\uc774\ube0c\ub7ec\ub9ac\uc9c0\ub9cc \uac1c\uc778/\uc0ac\uc5c5\uccb4\uc6a9 \uc6f9\ud398\uc774\uc9c0 \uc81c\uc791\uc6a9\uc73c\ub85c \uc4f0\uae30\uc5d0\ub3c4 \uc801\uc808\ud558\ub2e4. \ube14\ub85c\uadf8\uc640 Static page \uc81c\uc791\uc744 \uc9c0\uc6d0\ud558\uae30 \ub54c\ubb38\uc774\ub2e4. \ub2e4\ub9cc Markdown, React\uc5d0 \ub300\ud55c \uc774\ud574\uac00 \ubc18\ub4dc\uc2dc \ud544\uc694\ud558\ub2e4. \ub610\ud55c Docusaurus \uc21c\uc815 \uc0c1\ud0dc\ub97c \uac70\uc758 \uadf8\ub300\ub85c \uc720\uc9c0\ud558\uc9c0 \uc54a\ub294 \uc774\uc0c1 \uc18c\uc2a4 \ucf54\ub4dc \ucda9\ub3cc\uc774\ub098 \uae30\ud0c0 \ubc84\uadf8\uac00 \ubc1c\uc0dd\ud560 \uc218 \uc788\ub294\ub370 \ud504\ub85c\uadf8\ub798\ubc0d\uc774 \uac00\ub2a5\ud558\uc9c0 \uc54a\uc740 \uc0ac\ub78c\uc774\ub77c\uba74 \ub300\ucc98\uac00 \uc5b4\ub835\ub2e4\ub294 \ub2e8\uc810\uc774 \uc788\ub2e4. \uc560\ucd08\uc5d0 <\uc11c\ube44\uc2a4>\uac00 \uc544\ub2c8\ub77c <\ub77c\uc774\ube0c\ub7ec\ub9ac>\uae30 \ub54c\ubb38\uc5d0 \uac1c\ubc1c\uc790\uac00 \ud0c0\uae43\uc774\uace0, \ub2e4\ub9cc \ubaa8\ub4e0 \ud398\uc774\uc9c0\ub97c \uc9c1\uc811 \ub2e4 \uac1c\ubc1c\ud558\ub294 \uac83\uc774 \ubc88\uac70\ub85c\uc6b4 \uc0ac\ub78c\uc744 \uc704\ud574 \ud15c\ud50c\ub9bf\uacfc \uac19\uc740 \uc5ed\ud560\uc744 \ud55c\ub2e4\uace0 \ubcf4\ub294 \uac83\uc774 \ub9de\ub2e4. \uc774\ub7f0 \ud2b9\uc9d5 \ub54c\ubb38\uc5d0 \ub098\ucc98\ub7fc \uac1c\ubc1c\uc790\ub85c \uba39\uace0 \uc0b4 \uc21c \uc5c6\uc9c0\ub9cc, \ud504\ub85c\uadf8\ub798\ubc0d\uc744 \ud234\ucc98\ub7fc \uc0ac\uc6a9\ud558\ub294 \uae30\ucd08\uc801\uc778 \ub2a5\ub825\uc774 \uc788\ub294 \uc0ac\ub78c\uc5d0\uac8c\ub294 \uba74\ud5c8 \uc5c6\uc774 \ud3ec\ud06c\ub808\uc778\uc744 \uc368\uc11c \ub545\uc744 \ud30c\ub294 \uac83\uacfc \uac19\uc740(?) \ud6a8\uacfc\ub97c \ubc1c\ud718\ud560 \uc218 \uc788\ub2e4. \\n\\n---\\n\\n## \uc6c5\ub369\uc774 \ud30c\ub294 \ub370 \ud3ec\ud06c\ub808\uc778 \uc368\ubcf4\uae30\\n\\n\ucc98\uc74c \uac1c\uc778 \ud648\ud398\uc774\uc9c0\ub97c \ub9cc\ub4e4\uace0 \uc2f6\ub2e4\uace0 \uc0dd\uac01\ud588\uc744 \ub54c \uc0ac\uc2e4 \uc5b4\ub5a4 \ub0b4\uc6a9\uc744 \ubcf4\uc5ec\uc8fc\uace0 \uc2f6\uc740\uc9c0\uc5d0 \ub300\ud55c \uacc4\ud68d\uc740 \uad6c\uccb4\uc801\uc73c\ub85c \uc5c6\uc5c8\ub2e4. \uadf8\uc800 \uac1c\uc778\uc801\uc778, \uadf8\ub9ac\uace0 \ud64d\ubcf4\uc6a9 \uacf5\uac04\uc744 \uac16\uace0 \uc2f6\uc5c8\uace0, \ub354 \ub2e8\uc21c\ud558\uac8c\ub294 \ucf54\ub529 \uac1c\uc778 \ud504\ub85c\uc81d\ud2b8\ub97c \ud574\ubcf4\uace0 \uc2f6\uc5c8\ub2e4. <etc>(\uc6d0\ub798 \ubb34\uc5b8\uac00\ub97c \uc57c\ub9e4\ub85c \ubc30\uc6b4 \uc0ac\ub78c\uc774 \uc695\uc2ec\uc740 \ub354 \ub9ce\uc740 \ubc95\uc774\ub2e4.)</etc> \ud1f4\uc0ac \ud6c4 \uc790\uae30\uacc4\ubc1c \uc2dc\uac04\uc744 \uac00\uc9c0\uac8c \ub418\uba74\uc11c \uc9c4\uc9c0\ud558\uac8c \ud648\ud398\uc774\uc9c0 \uacc4\ud68d\uc744 \uc9dc\ubcf4\ub2c8, \uacb0\uacfc\uc801\uc73c\ub85c Docusaurus\ub97c \uc4f0\uaca0\ub2e4\ub294 \uc120\ud0dd\uc740 \ub2e4\uc18c \uc6c5\ub369\uc774 \ud30c\uaca0\ub2e4\uace0 \ud3ec\ud06c\ub808\uc778\uc744 \ub3d9\uc6d0\ud55c\uac8c \uc544\ub2d0\uae4c \ud558\ub294 \uc0dd\uac01\ub3c4 \ub4e4\uc5c8\ub2e4. \uadf8\ub7ec\ub098 \uc7a5\uae30\uc801\uc778 \uad00\uc810\uc5d0\uc11c\ub294 \uc5b4\ub5bb\uac8c \uc774 \uacf3\uc774 \ud655\uc7a5\ub420\uc9c0 \uc54c \uc218 \uc5c6\uace0, \ub354 \ub9ce\uc740 \uc790\uc720\ub3c4\ub97c \uac00\uc9c8 \uc218 \uc788\ub2e4\ub294 \uc810\uc5d0\uc11c, \uadf8\ub9ac\uace0 \ubb34\uc5c7\ubcf4\ub2e4 \uac1c\ubc1c \uacfc\uc815\uc5d0\uc11c \ub9ce\uc740 \uac83\uc744 \ubc30\uc6e0\uace0 \uc7ac\ubc0c\ub2e4\ub294 \uc810\uc5d0\uc11c \uc88b\uc740 \uc120\ud0dd\uc774\uc5c8\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4. \\n\\n---\\n\\n## \ubb34\uc5c7\uc744 \ubc30\uc6e0\uc744\uae4c\\n\\n### 1. \ub514\uc790\uc778 : Design System \uad6c\ud604\uc744 \uc704\ud55c Figma\uc758 Auto Layout, Property \uae30\ub2a5 \uc368\ubcf4\uae30\\n\\n\uc77c\ub2e8 \ub9cc\ub4e4\uace0 \ubcf4\uc790\ub294 \uc0dd\uac01\uc73c\ub85c \ub364\ubcbc\ub4e4\uc5c8\ub354\ub2c8 \uc608\uc0c1\ud558\uc9c0 \ubabb\ud55c \uc5b4\ub824\uc6c0\uc774 \uacf3\uacf3\uc5d0 \uc788\uc5c8\uace0, \uacb0\uacfc\uc801\uc73c\ub85c \ub354 \ub9ce\uc740 \uac78 \ubc30\uc6b8 \uc218 \uc788\uc5c8\ub2e4. \uc704\uc5d0 \uc801\uc5c8\ub4ef\uc774 \ud558\ub098\uc758 \uc18c\ud504\ud2b8\uc6e8\uc5b4 \uc81c\ud488\uc744 \ub9cc\ub4e4 \ub54c \uc9c4\ud589\ud558\ub294 \ub2e8\uacc4\ub97c \uac04\ub7b5\ud558\uac8c \uc9c4\ud589\ud55c \uc148\uc774\uc5c8\ub2e4. PM\uc73c\ub85c \uc77c\ud558\uba74\uc11c \uc606\uc5d0\uc11c \uad6c\uacbd\ub9cc \ud588\uc5c8\ub358 Design System(\uc774\ub77c\uace0 \ud558\uae30\uc5d4 \ubab9\uc2dc \ubd80\ub044\ub7fd\uc9c0\ub9cc) \uae30\ubc18\uc758 Component \uc694\uc18c\ub97c \ub9cc\ub4e4\uae30 \uc704\ud574 Figma\uc758 Auto Layout, Property \uae30\ub2a5\uc744 \uc368\ubcf4\uc558\ub2e4. Auto Layout \uc815\ub3c4\ub294 \uac00\ub054\uc529 Figma\ub85c \uac04\ub2e8\ud55c \ub514\uc790\uc778\uc744 \ud560 \ub54c \uc368\ubcf4\uae34 \ud588\uc9c0\ub9cc \uc774\ubc88\uc5d0\ub294 \uc790\ub3d9 \uc0dd\uc131\ub418\ub294 CSS\ub97c \uac19\uc774 \ubcf4\uba74\uc11c \ub369\ub2ec\uc544 CSS \ub2e4\ub8e8\ub294 \ubc95\ub3c4 \uc775\ud790 \uc218 \uc788\uc5c8\ub2e4. \\n\\n### 2. \uac1c\ubc1c \uc900\ube44 : React\uc758 \ucee8\uc149 \uc774\ud574, \ud3c9\ubc94\ud55c HTML + CSS + JS\ub85c \uad6c\uc131\ub418\ub294 \uc6f9\ud398\uc774\uc9c0 \uac1c\ubc1c\uacfc \ubb34\uc5c7\uc774 \ub2e4\ub97c\uae4c\\n\\n\uc608\uc804 \ud68c\uc0ac\uc5d0\uc11c \ucc98\uc74c Docusaurus\ub97c \uc4f8 \ub54c\ub294 React\ub97c \uc804\ud600 \ubaa8\ub974\ub294 \uc0c1\ud0dc\uc600\uace0, \uadf8\ub798\ub3c4 \uc0ac\uc6a9\uc740 \uac00\ub2a5\ud588\uc9c0\ub9cc \uac00\uc774\ub4dc\uc5d0 \uc801\ud78c \uba85\ub839\uc5b4\ub97c \uc804\ud600 \uc774\ud574\ud558\uc9c0 \ubabb\ud558\uace0 \uae30\uacc4\uc801\uc73c\ub85c \ub530\ub77c\ud558\ub2e4\ubcf4\ub2c8 \ucee4\uc2a4\ud130\ub9c8\uc774\uc9d5\uc774\ub098 \ubb38\uc81c \uc0c1\ud669\uc5d0\uc11c \ub300\ucc98\ud560 \uc218 \uc788\ub294 \uc5ec\uc9c0\uac00 \uad49\uc7a5\ud788 \uc801\uc5c8\ub2e4. \uadf8\ub798\uc11c \uc774\ubc88\uc5d0\ub294 \uae30\ucd08 \uac1c\ub150\uc774\ub77c\ub3c4 React\ub97c \uacf5\ubd80\ud558\uace0 \uc368\ubcf4\uc790\ub294 \uc0dd\uac01\uc744 \ud588\uace0 \ub178\ub9c8\ub4dc \ucf54\ub354\uc758 \ubb34\ub8cc \uac15\uc758\uac00 \ud070 \ub3c4\uc6c0\uc774 \ub418\uc5c8\ub2e4. \\n\\n### 3. \uac1c\ubc1c : \ub514\uc790\uc778 \ucee4\uc2a4\ud130\ub9c8\uc774\uc9d5\uacfc Docusaurus Swizzle \uc774\ud574\ud558\uae30\\n\\nDocusaurus \uc124\uce58 \uc9c1\ud6c4 \ubcf4\uac8c \ub418\ub294 \uae30\ubcf8\uc801\uc778 \ub514\uc790\uc778 \ub808\uc774\uc544\uc6c3\uc740 \uc694\uc998 \uac00\uc7a5 \ud754\ud788 \ubcfc \uc218 \uc788\ub294 \uae54\ub054\ud55c, \ubc18\uc751\ud615 \ub514\uc790\uc778\uc73c\ub85c \uad6c\uc131\ub418\uc5b4 \uc788\ub2e4. \uadf8\ub7fc\uc5d0\ub3c4 \ub2f9\uc5f0\ud788 \ucee4\uc2a4\ud130\ub9c8\uc774\uc9d5\uc740 \ud544\uc694\ud558\ub2e4. \uc778\ub371\uc2a4 \ud398\uc774\uc9c0\uc758 \ub0b4\uc6a9\uc744 \uc804\ubd80 \ubc14\uafd4\uc57c \ud558\uace0, \uc0c9\uc0c1, \ud3f0\ud2b8, \uac04\uaca9 \ub4f1\uc744 \uc870\uc815\ud574\uc57c \ud55c\ub2e4. \uc774 \uc815\ub3c4 \uc14b\ud305\uc740 \uc608\uc0c1\ud588\ub294\uc9c0 \uc124\uce58 \uc9c1\ud6c4\uc5d0 \uc774\ubbf8 style.css \ud30c\uc77c\uc774 \uc0dd\uc131\ub418\uc5b4 \uc788\ub2e4. \uadf8\ub798\uc11c CSS \ub9cc\uc73c\ub85c \uac00\ub2a5\ud55c \ubcc0\ud654\ub294 React\uc5d0 \ub300\ud55c \uc774\ud574\uac00 \uc804\ud600 \uc5c6\uc5b4\ub3c4 \uac00\ub2a5\ud558\ub2e4. \ub2e4\ub9cc \ub3d9\uc801\uc778 \ubcc0\uc218\uac12\uc744 \ud65c\uc6a9\ud574\uc57c \ud558\ub294 \ub4f1, \uc815\uc801\uc778 \ub514\uc790\uc778 \ubcc0\uacbd \uc774\uc0c1\uc758 \ucee4\uc2a4\ud130\ub9c8\uc774\uc9d5\uc744 \ud558\ub824\uba74 Docusaurus \ub77c\uc774\ube0c\ub7ec\ub9ac \ucf54\ub4dc\ub97c \uc9c1\uc811 \uac74\ub4dc\ub9ac\ub294 \uc791\uc5c5\uc774 \ud544\uc694\ud558\ub2e4. \uc774\ubc88 \ud648\ud398\uc774\uc9c0 \uc81c\uc791\uc5d0\uc11c \uac00\uc7a5 \ub9ce\uc740 \uc0bd\uc9c8\uc744 \ud558\uace0 \uc2dc\uac04\uc744 \ubcf4\ub0b8 \ub2e8\uacc4\uac00 \ubc14\ub85c \uc774 \ub54c\uc600\ub2e4. \ub0b4\uac00 \uc6d0\ud588\ub358 \uac78 100% \uad6c\ud604\ud560 \uc218 \uc5c6\uc5c8\uc9c0\ub9cc \uadf8\ub798\ub3c4 \ub300\ubd80\ubd84\uc740 \uad6c\ud604\ud558\uace0, \ub098\uc911\uc5d0 \uc751\uc6a9\ud560 \uc218 \uc788\uaca0\ub2e4\ub294 \uc790\uc2e0\uac10\uc744 \uc5bb\uc5c8\ub2e4. \\n\\nDocusaurus\ub294 Theme \ucee4\uc2a4\ud130\ub9c8\uc774\uc9d5\uc744 \uc9c0\uc6d0\ud558\ub294 \ub370 \uc774\ub97c [Swizzle](https://docusaurus.io/docs/swizzling)\uc774\ub77c\uace0 \uc18c\uac1c\ud558\uace0 \uc788\ub2e4. \uac04\ub2e8\ud788 \ub9d0\ud558\uba74 Docusaurus\uc5d0 \uc774\ubbf8 \uad6c\ud604\ub418\uc5b4 \uc788\ub294 React Component \ud30c\uc77c\uc744 \uc0ac\uc6a9\uc790\uac00 \uc218\uc815\ud560 \uc218 \uc788\ub294 \uc7a5\uce58\uc774\ub2e4. \ubb3c\ub860 \uc774\ub7f0 \ubcc4\ub3c4\uc758 \uc7a5\uce58\uac00 \uc5c6\ub354\ub77c\ub3c4 \ub77c\uc774\ube0c\ub7ec\ub9ac\uc778 \uc774\uc0c1 \uc5b4\ub5bb\uac8c\ub4e0 \uc218\uc815\uc740 \uac00\ub2a5\ud558\uc9c0\ub9cc \uc774\ud6c4 \ub77c\uc774\ube0c\ub7ec\ub9ac \ud328\uce58\uc5d0 \ub530\ub978 \ud638\ud658\uc131\uc744 \uc7a5\ub2f4\ud558\uae30 \uc5b4\ub835\uace0, \ub098\uc640 \uac19\uc740 \ucd08\ubcf4 \uac1c\ubc1c\uc790\uc5d0\uac8c\ub294 \ubd88\uac00\ub2a5\ud55c \ubc29\ubc95\uc774\ub2e4. \ud638\ud658\uc131\uc744 \uc644\uc804\ud788 \ubcf4\uc7a5\ud560 \uc21c \uc5c6\uc9c0\ub9cc \ub098\uc758 \ucee4\uc2a4\ud130\ub9c8\uc774\uc9d5\uc774 \ubbf8\uce60 \uc601\ud5a5\ub825\uc744 \ud310\ub2e8\ud558\uba74\uc11c \uc190\uc27d\uac8c \ub0b4\uac00 \uc218\uc815\ud574\uc57c \ud558\ub294 \ud30c\uc77c\uc5d0 \uc811\uadfc\ud560 \uc218 \uc788\ub3c4\ub85d \ub3c4\uc6c0\uc744 \uc8fc\ub294 \uac83\uc774 Swizzle\uc758 \uc5ed\ud560\uc774\ub2e4. \\n\\n### 4. \ubc30\ud3ec : Github Pages \uc0ac\uc6a9\ubc95 \uc775\ud788\uae30\\n\\n<full-image>![Github Pages \ud648\ud398\uc774\uc9c0 \uba54\uc778 \ud654\uba74](./homepage_githubpages.png)</full-image>\\n<full-caption>Github Pages \ud648\ud398\uc774\uc9c0</full-caption>\\n\\n\uac1c\ubc1c\uc774 \ub05d\ub0ac\uc73c\uba74 \uc774\uc81c \ubc30\ud3ec\ud574\uc57c \ud560 \ub54c\ub2e4. \uba87 \ub144 \uc804\uc5d0 \uc6f9\uc0ac\uc774\ud2b8 \uac1c\ubc1c\uc744 \ubc30\uc6b0\ub358 \ucd08\uae30\uc5d0 \ubc30\ud3ec\uc640 \ub3c4\uba54\uc778 \uc5f0\uacb0 \uac1c\ub150\uc744 \uc774\ud574\ud558\uace0 \uc0ac\uc6a9\ubc95\uc744 \uc775\ud788\uae30\uae4c\uc9c0 \uaf64 \uc5b4\ub824\uc6c0\uc744 \uacaa\uc5c8\uc5c8\ub2e4. \uadf8 \ub54c\ub294 \uc544\uc8fc \ub2e8\uc21c\ud558\uac8c HTML + CSS + Jquery\ub85c \uc5f0\uc2b5\ud558\ub358 \uc2dc\uc808\uc774\ub77c \uc6f9\ud638\uc2a4\ud305 \uc0ac\uc774\ud2b8\uc5d0 \ud638\uc2a4\ud305\uacfc \ub3c4\uba54\uc778\uc744 \uacb0\uc81c\ud558\uace0 FTP\ub85c \uc18c\uc2a4 \ucf54\ub4dc\ub97c \uc62c\ub9b0\ub2e4\ub294\uac8c \ub0b4\uac00 \uc54c\uace0 \uc788\ub294 \ubc30\ud3ec \ubc29\uc2dd\uc774\uc5c8\ub2e4. \uc774 \ubc29\uc2dd\uc740 \ud604\uc7ac\ub3c4 \uc720\ud6a8\ud558\uc9c0\ub9cc \uc6d4\ubcc4 \uc720\uc9c0 \ube44\uc6a9\uc774 \ub4e0\ub2e4\ub294 \ub2e8\uc810\uc774 \uc788\ub2e4. \uadf8\ub798\uc11c \uac1c\uc778 \ud648\ud398\uc774\uc9c0 \uc81c\uc791\uc5d0 \uc5ec\ub7ec \ubb34\ub8cc \ud638\uc2a4\ud305 \ubc29\ubc95\uc774 \ud65c\uc6a9\ub418\uace4 \ud558\ub294 \ub370 [Github Pages](https://pages.github.com)\ub294 \uadf8 \uc218\ub2e8 \uc911 \ud558\ub098\uc774\ub2e4. \\n\\nGithub\uc5d0\uc11c \uacc4\uc815\ub2f9 1\uac1c\uc758 username.github.io \ub3c4\uba54\uc778\uc744 \uc9c0\uc6d0\ud558\ub294\ub370 Github repository\uc640 \uc5f0\uacb0\ub418\uc5b4 \ud638\uc2a4\ud305 \uc11c\ubc84\uc640 \ub3c4\uba54\uc778\uc744 \ud55c \ubc88\uc5d0 \ud574\uacb0\ud560 \uc218 \uc788\ub2e4. \ucc98\uc74c \uc368\ubd24\uc744 \ub54c\ub294 \ubc30\ud3ec \ud6c4\uc5d0 \uc81c\ub300\ub85c \ub3c4\uba54\uc778\uc5d0 \uc6f9\uc0ac\uc774\ud2b8\uac00 \ud45c\uc2dc\ub418\uc9c0 \uc54a\uc544\uc11c \uc560\ub97c \uba39\uc5c8\ub294\ub370, \uc774\ubc88 \ud504\ub85c\uc81d\ud2b8\uc5d0\uc11c\ub294 \uc6d0\ud65c\ud558\uac8c \ubc30\ud3ec\ub97c \uc9c4\ud589\ud560 \uc218 \uc788\uc5c8\ub2e4. \\n\\n---\\n\\n\uc774\uc81c\ubd80\ud130 \uc774\uc5b4\uc9c0\ub294 \ud3ec\uc2a4\ud305\uc5d0\uc11c \uadf8\ub3d9\uc548 \ubc30\uc6e0\ub358 \uac83\uacfc \ubb38\uc81c\ub97c \ud574\uacb0\ud55c \uacfc\uc815\uc744 \uc21c\uc11c\ub300\ub85c \uae30\ub85d\ud574\ub458 \uc0dd\uac01\uc774\ub2e4."},{"id":"mac-gestures-without-trackpad","metadata":{"permalink":"/blog/mac-gestures-without-trackpad","source":"@site/blog/2022-06-12-mac-gestures-without-trackpad/2022-06-12-mac-gestures-without-trackpad.mdx","title":"\ub9e4\uc9c1 \ub9c8\uc6b0\uc2a4, \ud2b8\ub799\ud328\ub4dc \uc5c6\uc774 \ub9e5 \uc81c\uc2a4\ucc98 \ud65c\uc6a9\ud558\uae30","description":"\uc190\uc774 \uc544\ud30c\ub3c4 \ud2b8\ub799\ud328\ub4dc\ub97c \uc5b5\uc9c0\ub85c \uc4f0\ub294 \uc774\uc720\uc778 \ub9e5 \uc81c\uc2a4\ucc98\ub97c \ud2b8\ub799\ud328\ub4dc \uc5c6\uc774 \uc4f0\ub294 \ubc29\ubc95","date":"2022-06-12T00:00:00.000Z","formattedDate":"2022\ub144 6\uc6d4 12\uc77c","tags":[{"label":"\uc0dd\uc0b0\uc131","permalink":"/blog/tags/\uc0dd\uc0b0\uc131"},{"label":"\ud234 \ud65c\uc6a9 \ud301","permalink":"/blog/tags/\ud234-\ud65c\uc6a9-\ud301"}],"readingTime":14.92,"hasTruncateMarker":true,"authors":[{"name":"\ud558\ubbf8\uc5f0","titile":"\uaca8\uc6b8\uc774\ubd88\uc758 \uc544\uce74\uc774\ube0c","imageURL":"https://avatars.githubusercontent.com/u/67229997?v=4","key":"winter"}],"frontMatter":{"title":"\ub9e4\uc9c1 \ub9c8\uc6b0\uc2a4, \ud2b8\ub799\ud328\ub4dc \uc5c6\uc774 \ub9e5 \uc81c\uc2a4\ucc98 \ud65c\uc6a9\ud558\uae30","description":"\uc190\uc774 \uc544\ud30c\ub3c4 \ud2b8\ub799\ud328\ub4dc\ub97c \uc5b5\uc9c0\ub85c \uc4f0\ub294 \uc774\uc720\uc778 \ub9e5 \uc81c\uc2a4\ucc98\ub97c \ud2b8\ub799\ud328\ub4dc \uc5c6\uc774 \uc4f0\ub294 \ubc29\ubc95","slug":"mac-gestures-without-trackpad","authors":"winter","tags":["\uc0dd\uc0b0\uc131","\ud234 \ud65c\uc6a9 \ud301"],"keywords":["\ub9e5 \uc81c\uc2a4\ucc98","\ud2b8\ub799\ud328\ub4dc","\ub9e4\uc9c1 \ub9c8\uc6b0\uc2a4"],"date":"2022-06-12T00:00:00.000Z"},"prevItem":{"title":"\uc6c5\ub369\uc774 \ud30c\ub294 \ub370 \ud3ec\ud06c\ub808\uc778 \uc368\ubcf4\uae30","permalink":"/blog/create-personal-website-with-docusaurus"}},"content":"## \uc870\uae08 \uae34 \uc11c\ub860, Mac OS \uc81c\uc2a4\ucc98\uac00 \uc65c \uc911\uc694\ud55c\uac00.\\n\\nMac OS\ub97c \uc4f0\uba74\uc11c \uc5ec\ub7ec \uc81c\uc2a4\ucc98 \uae30\ub2a5(Mission Control, Desktop navigation)\uc744 \uc4f0\uc9c0 \uc54a\ub294\ub2e4\uba74, Mac OS \ud2b9\uc720\uc758 \uc7a5\uc810\uc744 (\uacfc\uc7a5 \uc880 \ubcf4\ud0dc\uc11c) \uc808\ubc18\uc740 \ubc84\ub9ac\uace0 \uc4f0\ub294 \uac83\uacfc \ub9c8\ucc2c\uac00\uc9c0\ub77c\uace0 \uc0dd\uac01\ud55c\ub2e4. \uadf8\ub9cc\ud07c \ud3c9\uc18c\uc5d0 \uc544\uc8fc \uc798 \ud65c\uc6a9\ud558\ub294 \ud3b8\uc778\ub370, \ucef4\ud4e8\ud130\ub97c \uc4f8 \ub54c\ub294 \uc5b8\uc81c\ub098 \uc5ec\ub7ec \ud504\ub85c\uadf8\ub7a8\uc744 \uc2e4\ud589\uc2dc\ucf1c\ub193\uace0 \uadf8 \uc0ac\uc774\ub97c \uc624\uac00\uba74\uc11c \uc791\uc5c5\ud558\uae30 \ub54c\ubb38\uc774\ub2e4. \uadf8\ub807\ub2e4\uba74 \uc790\uc5f0\uc2a4\ub7fd\uac8c, \ucf1c\ub193\uc740 \uc5ec\ub7ec \ud504\ub85c\uadf8\ub7a8\uc744 \uc5b4\ub5bb\uac8c \uc815\ub9ac\ud558\uace0 \ubc30\uce58\ud560 \uc9c0\uac00 \uc911\uc694\ud574\uc9c4\ub2e4. \ud504\ub85c\uadf8\ub7a8\uc744 \uc624\uac00\ub294 \uc0ac\uc774 \uc2dc\uac04\uc744 \uc5bc\ub9c8\ub098 \uc904\uc77c \uc218 \uc788\ub290\ub0d0\uac00, \ucf1c\ub193\uc740 \ud504\ub85c\uadf8\ub7a8\uc774 \ub9ce\uc544\uc9c8\uc218\ub85d \uc0dd\uae30\ub294 \ubc88\uac70\ub85c\uc6c0\uc744 \uc904\uc77c \uc218 \uc788\uae30 \ub54c\ubb38\uc774\ub2e4.\\n\\n\x3c!--truncate--\x3e\\n\\n<full-image>![\ud3c9\uc18c \ub0b4 Mac Spaces \uae30\ubcf8 \uc0c1\ud0dc](./my-mac-spaces.png)</full-image>\\n<full-caption>\\n\ud3c9\uc18c \ub0b4 Mac Spaces \uae30\ubcf8 \uc0c1\ud0dc.\\n\uc774 \ud3ec\uc2a4\ud2b8\ub97c \uc4f0\uba74\uc11c \uc870\uc0ac\ud558\ub290\ub77c \ucc98\uc74c \uc54c\uac8c \ub41c \uc0ac\uc2e4\uc778\ub370, Mac\uc5d0\uc11c Mission Control\uc744 \uc2e4\ud589\ud588\uc744 \ub54c \ud654\uba74 \uc0c1\ub2e8\uc5d0 \ub098\uc624\ub294 Desktop \ubaa9\ub85d\uc744 [Spaces \ub9c9\ub300](https://support.apple.com/ko-kr/HT204100)\ub77c\uace0 \ubd80\ub978\ub2e4\uace0 \ud55c\ub2e4.\\n</full-caption>\\n\\n\uc608\ub97c \ub4e4\uba74, \ub098\ub294 \ud3c9\uc18c Slack\uc744 \uc804\uccb4 \ucc3d\uc73c\ub85c \ub9cc\ub4e0 \ud6c4 Desktop \uc67c\ucabd\uc5d0, Figma \ud639\uc740 VS Code \ucc98\ub7fc \uc804\uccb4 \ud654\uba74\uc73c\ub85c \uace0\uc815\ud574\ub193\uace0 \uc368\ub3c4 \ubd88\ud3b8\ud558\uc9c0 \uc54a\uc740 \ud504\ub85c\uadf8\ub7a8\uc744 Desktop \uc624\ub978\ucabd\uc5d0 \ub450\uace0 \uc4f0\ub294 \ud3b8\uc774\ub2e4. \ud2b9\ud788, 2\uac1c\uc758 \ud504\ub85c\uadf8\ub7a8\uc744 \uc624\uac00\ub294\ub370 \ud654\uba74 \ubd84\ud560\uc744 \ud558\uae30\uc5d4 \ub2f5\ub2f5\ud55c \uc791\uc5c5\uc744 \ud574\uc57c \ud558\ub294 \uacbd\uc6b0 \ud55c \ucabd\uc740 Desktop\uc5d0, \ub2e4\ub978 \ud55c\ucabd\uc740 \uc804\uccb4 \ud654\uba74\uc73c\ub85c \ub9cc\ub4e4\uace0 \uc88c\uc6b0 \uc774\ub3d9\ud558\uba74\uc11c \uc4f0\uba74 \ud3b8\ud574\uc9c4\ub2e4. \uc9c0\uae08 \uc774 \ud3ec\uc2a4\ud2b8\ub97c \uc4f8 \ub54c\ub3c4 Desktop\uc5d0 Notion, \uc624\ub978\ucabd\uc5d0 Figma\ub97c \ub450\uace0 \uc774\ubbf8\uc9c0\ub97c \ub9cc\ub4e4\uba74\uc11c \uc791\uc5c5\ud558\uace0 \uc788\ub2e4. \\n\\n<full-image>![\ud3c9\uc18c \ub0b4 \ucc45\uc0c1 \uc704 \ub514\uc2a4\ud50c\ub808\uc774\uc758 \ubc30\uce58\ub3c4](./my-displays.png)</full-image>\\n<full-caption>\\n\ud3c9\uc18c \ub0b4 \ucc45\uc0c1 \uc704 \ub514\uc2a4\ud50c\ub808\uc774\uc758 \ubc30\uce58\ub3c4.\\n\uc544\uc774\ud328\ub4dc \ubbf8\ub2c8\ub97c \uc0ac\uc6a9\uc911\uc774\ub77c Sidecar\ub294 \uc74c\uc545 \ud50c\ub808\uc774\uc5b4\ub098 \uc0c1\uc2dc \ucf1c\ub193\uc544\uc57c \ud558\ub294 \ub178\uc158 Todo \ud398\uc774\uc9c0 \uc815\ub3c4\ub97c \ucf1c\ub450\ub294 \ud3b8\uc774\ub2e4.\\n</full-caption>\\n\\n\ub2e4\ub9cc, \uc88c\uc6b0 \ud654\uba74\uc744 \ud65c\uc6a9\ud55c\ub2e4\uace0 \ud574\ub3c4, \uac1c\uc218\uac00 \ub298\uc5b4\ub098\uba74 \uc88c\uc6b0 \uc774\ub3d9\ud558\ub294 \ubc88\uac70\ub85c\uc6c0\uc774 \ub354 \ucee4\uc9c0\uae30 \ub54c\ubb38\uc5d0 Desktop \uc88c\uc6b0 1\uac1c\uc5d0\uc11c \ub354 \ub9ce\uc774 \uc804\uccb4 \ud654\uba74\uc744 \uc0ac\uc6a9\ud558\uc9c4 \uc54a\ub294 \ud3b8\uc774\ub2e4. \ub300\uc2e0, \uc678\uc7a5 \ubaa8\ub2c8\ud130\uc640 \uc544\uc774\ud328\ub4dc Sidecar\ub97c \uac19\uc774 \uc0ac\uc6a9\ud558\ub294 \uacbd\uc6b0 Desktop\uc758 \uac1c\uc218\uac00 3\uac1c\ub85c \ub298\uc5b4\ub098\uae30 \ub54c\ubb38\uc5d0 \uc591 \uc606\uc5d0 \ubc30\uce58\ud560 \uc218 \uc788\ub294 \uc804\uccb4 \ud654\uba74 \ud504\ub85c\uadf8\ub7a8\uc758 \uac1c\uc218\ub3c4 6\uac1c\uae4c\uc9c0 \ub298\uc5b4\ub098\uac8c \ub41c\ub2e4. \ubb3c\ub860, \uc774\ub7f0 \uc0ac\uc6a9 \ubc29\uc2dd\uc740 Destkop Navigation\uc744 \ube60\ub974\uac8c \ud560 \uc218 \uc788\ub2e4\ub294 \uc804\uc81c\uac00 \ud544\uc694\ud558\ub2e4. Desktop\uc5d0\ub294 \uc790\uc798\ud55c \ud504\ub85c\uadf8\ub7a8\uc744 \uc5ec\ub7ff \ucf1c\ub193\uae30 \ub54c\ubb38\uc5d0 Mission Control\ub3c4 \ud544\uc218\ub2e4. \ubcf4\ud1b5 \uc774\uac78 \ud574\uacb0\ud558\uae30 \uc704\ud574 \ub9e4\uc9c1 \ub9c8\uc6b0\uc2a4, \ud639\uc740 \ud2b8\ub799\ud328\ub4dc\ub97c \uc0ac\uc6a9\ud55c\ub2e4. \ud558\uc9c0\ub9cc \uc774 \ubc29\ubc95\uc740 \uc4f0\uace0 \uc2f6\uc9c0 \uc54a\uc558\ub2e4. \\n\\n![\ud2b8\ub799\ud328\ub4dc \ub9c8\uc6b0\uc2a4\ub97c \uc4f0\ub294 \uc7a5\uba74](https://images.pexels.com/photos/1109543/pexels-photo-1109543.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2)\\n\\n\\n## \uae30\uacc4 \ub355\ud6c4\uc758 \ub9c8\uc6b0\uc2a4 \uc2dc\ud589\ucc29\uc624\\n\\n\ud2b8\ub799\ud328\ub4dc\ub97c \uae30\uaecf \uad6c\ub9e4\ud574\ub193\uace0 \uba87 \ub2ec\uac04 \uc368\ubcf8 \ud6c4\uc5d0 \uae68\ub2ec\uc558\ub294\ub370, \uc77c\ubc18\uc801\uc778 \ub9c8\uc6b0\uc2a4\uc5d0 \ube44\ud574 \uc190\uac00\ub77d \ub05d\uc5d0 \ub354 \ud798\uc774 \uac00\ub294 \uad6c\uc870 \ub54c\ubb38\uc5d0 \uc624\ub798 \uc4f0\uae30 \uc5b4\ub824\uc6e0\ub2e4. \ud2b9\ud788 \ub4dc\ub798\uadf8 \uc564 \ub4dc\ub86d\uc744 \ud574\uc57c \ud560 \uacbd\uc6b0 \uba87 \ubc30\ub098 \ud798\uc774 \ub354 \ub4e4\uc5b4\uac00\uae30 \ub54c\ubb38\uc5d0 \uac04\ub2e8\ud55c \uc774\ubbf8\uc9c0 \ud3b8\uc9d1 \uc791\uc5c5\uc744 \ud560 \uc77c\uc774 \uc0dd\uae30\uba74 \uc190\uc774 \uc544\ud30c\uc11c \uc9dc\uc99d\uc774 \ub0a0 \uc9c0\uacbd\uc774\uc5c8\ub2e4. \ub9e4\uc9c1 \ub9c8\uc6b0\uc2a4\ub294 \uc81c\ub300\ub85c \uc368\ubcf8 \uc801\uc774 \uc5c6\uc9c0\ub9cc \ud2b8\ub799\ud328\ub4dc\uc640 \ube44\uc2b7\ud558\uac8c \ub9c8\uc6b0\uc2a4\uc758 \ub192\uc774\uac00 \uad49\uc7a5\ud788 \ub0ae\uc544\uc11c \uc190 \uac74\uac15\uc5d0 \uc88b\uc9c0 \uc54a\uc744 \uac83 \uac19\ub2e4\ub294 \uc778\uc0c1\uc740 \uadf8\ub300\ub85c\uc600\ub2e4.\\n\\n<half-image>![MX Master \uc2dc\ub9ac\uc988](./mx-master.png)</half-image>\\n<half-image>![MX Anywhere 3](./mx-anywhere-3.png)</half-image>\\n\\n<half-caption>\ub0b4\uac90 \ub108\ubb34 \ubb34\uac70\uc6e0\ub358 MX Master \uc2dc\ub9ac\uc988</half-caption>\\n<half-caption>\ub2f9\uc2dc \uad6c\ub9e4\ud588\ub358 MX Anywhere \uc2dc\ub9ac\uc988</half-caption>\\n\\n\uadf8\ub798\uc11c \ucc98\uc74c \uc2dc\ub3c4\ud55c \ubc29\ubc95\uc740 \ub85c\uc9c0\ud14d\uc758 MX Anywhere \uc2dc\ub9ac\uc988 \ub9c8\uc6b0\uc2a4\ub97c \uc0ac\ub294 \uac83\uc774\uc5c8\ub2e4.\ubcf4\ud1b5 \uace0\uae09\ud615 \uc0ac\ubb34\uc6a9 \ub9c8\uc6b0\uc2a4\ub97c \ucc3e\ub2e4\ubcf4\uba74 \ub098\uc624\ub294 \uc81c\ud488\uc774\ub2e4. \uad00\ub828 \uc2dc\ub9ac\uc988\ub85c Master 3S \uc81c\ud488\ub3c4 \uc720\uba85\ud558\uc9c0\ub9cc, \uac00\uaca9\uc774 10\ub9cc\uc6d0 \uc911\ubc18\ub300\ub85c \uc880 \ub354 \ube44\uc30c\uace0, \uc9c0\uc778\uc5d0\uac8c \uc7a0\uae50 \ube4c\ub824\uc11c \uc368\ubcf8 \uac10\uc0c1\uc73c\ub85c\ub294 \ubb34\uac8c\uac10\uc774 \uaf64 \uc788\ub294 \ud3b8\uc774\ub77c \ubd80\ub2f4\uc2a4\ub7ec\uc6e0\ub2e4. \uadf8\ub798\uc11c \uac00\uaca9\uc774 10\ub9cc\uc6d0 \uc548\ucabd\uc73c\ub85c \uc870\uae08 \ub354 \uc2f8\uace0 \uae30\ub2a5 \uc2a4\ud399\uc740 \uac70\uc758 \ub3d9\uc77c\ud55c Anywhere \uc2dc\ub9ac\uc988\ub97c \uad6c\ub9e4\ud574\uc11c \uc0ac\uc6a9\ud588\ub294\ub370 \uaf64 \ub9cc\uc871\ud588\ub2e4. \\n\\n![\ub85c\uc9c0\ud14d \ub9c8\uc6b0\uc2a4 \ucee4\uc2a4\ud140 \uc18c\ud504\ud2b8\uc6e8\uc5b4](https://resource.logitech.com/w_1800,h_1800,c_limit,q_auto,f_auto,dpr_1.0/d_transparent.gif/content/dam/logitech/en/software/options/logi-options-feature-desktop.png?v=1)\\n\\n\uc774 \uc81c\ud488\uc73c\ub85c \ubb38\uc81c\uac00 \ud574\uacb0\ub410\ub358 \uc774\uc720\ub294, \ub85c\uc9c0\ud14d\uc5d0\uc11c \uc81c\uacf5\ud558\ub294 Logitech Options\ub77c\ub294 \uc18c\ud504\ud2b8\uc6e8\uc5b4\ub85c \uac01 \ubc84\ud2bc\uc5d0 \ub9f5\ud551\ub41c \ub3d9\uc791\uc744 \ucee4\uc2a4\ud130\ub9c8\uc774\uc9d5\ud560 \uc218 \uc788\ub294\ub370, \ud504\ub9ac\uc14b\uc5d0 \uc544\uc598 Mac OS\ub97c \uace0\ub824\ud55c \uc81c\uc2a4\ucc98 \uc870\ud569\uc774 \uc124\uc815\ub418\uc5b4 \uc788\uc5c8\uae30 \ub54c\ubb38\uc774\ub2e4. \ubb38\uc81c\ub294 1\ub144 \uc880 \uc548\ub418\uac8c \uc4f0\uae30 \uc2dc\uc791\ud558\ub2c8\uae4c \ub9c8\uc6b0\uc2a4 \uc5f0\uacb0\uc774 \ubd88\uc548\uc815\ud574\uc9c0\ub294 \ubb38\uc81c\uac00 \uc0dd\uacbc\ub2e4\ub294 \uc810\uc774\ub2e4. \uadf8\ub7f0\ub358 \uc911, \ucef4\ud4e8\ud130\ub97c \ubc14\uafb8\uace0 \ud68c\uc0ac\ub97c \uc62e\uae30\uba70 \uc815\uc2e0\uc5c6\ub294 \uc640\uc911\uc5d0 \uace0\uc7a5\ub09c \uae30\uacc4\ub098 \ub2e4\ub984 \uc5c6\ub358 \ub85c\uc9c0\ud14d \ub9c8\uc6b0\uc2a4\ub294 \uc5b4\ub518\uac00 \uc11c\ub78d\uc5d0 \ub123\uc5b4\ub450\uc5c8\ub2e4. \uc774\uc81c \uc0c8 \ub9c8\uc6b0\uc2a4\ub97c \uc0ac\uc57c \ud558\ub294\ub370\u2026  \ubb38\ub4dd \uc0dd\uac01\uc774 \ub0ac\ub2e4. \uadf8 \ube44\uc2fc \ub85c\uc9c0\ud14d \ub9c8\uc6b0\uc2a4\ub85c\ub3c4 \uacb0\uad6d \ud0a4 \ub9f5\ud551\uc73c\ub85c \ud574\uacb0\ud588\ub294\ub370, \uadf8\ub7fc \uc77c\ubc18 \ubc84\ud2bc \ub9ce\uc740 \ub9c8\uc6b0\uc2a4 \uc0ac\ub2e4\uac00 \ub0b4\uac00 \uc9c1\uc811 \ud0a4 \ub9f5\ud551\uc744 \ud558\uba74 \ub418\uc9c0 \uc54a\uc744\uae4c?\\n\\n\\n## \uc774\ub7f4\uac70\uba74 \ube44\uc2fc \ub9c8\uc6b0\uc2a4\ub294 \uc65c \uc0c0\uc744\uae4c\u2026\\n\\n<full-image>![\uce74\ub77c\ubc14\uc774\ub108 \uc0ac\uc6a9 \ud654\uba74 \uc2a4\ud06c\ub9b0 \ucea1\ucc98](./screen-capture-karabiner.png)</full-image>\\n\\n\uc774 \uc0dd\uac01\uc774 \uac00\ub2a5\ud588\ub358 \uc774\uc720\ub294 \uc774\ubbf8 [Karabiner-elements\ub77c\ub294 \uc18c\ud504\ud2b8\uc6e8\uc5b4](https://karabiner-elements.pqrs.org/)\ub97c \uc0ac\uc6a9\ud558\ub358 \uc911, \ub9c8\uc6b0\uc2a4 \uc81c\uc5b4\ub3c4 \uac00\ub2a5\ud558\ub2e4\ub294 \uc810\uc744 \uc5b4\ub834\ud48b\uc774 \uc54c\uace0 \uc788\uc5c8\uae30 \ub54c\ubb38\uc774\ub2e4. Mac OS\ub97c \uace0\ub824\ud558\uc9c0 \uc54a\ub294 \uc77c\ubc18 \ud0a4\ubcf4\ub4dc\ub97c \uc0ac\uc6a9\ud560 \ub54c, option/command \ud0a4 \uc124\uc815\uc744 \uc704\ud574\uc11c \uc124\uce58\ud558\uac8c \ub418\ub294 \ud504\ub85c\uadf8\ub7a8\uc774\ub2e4. \ucef4\ud4e8\ud130\uc5d0 \ub9c8\uc6b0\uc2a4\ub97c \uc5f0\uacb0\ud574\ub193\uc740 \uc0c1\ud0dc\uc5d0\uc11c \ub9c8\uc6b0\uc2a4 \ubc84\ud2bc\uc744 \uc81c\uc5b4\ud560 \uc218 \uc788\ub3c4\ub85d \uc124\uc815\uc744 \ucf1c\uba74, \uac70\uc758 \uc81c\uc57d\uc5c6\uc774 \uc790\uc720\uc790\uc7ac\ub85c \ub9c8\uc6b0\uc2a4 \ud0a4\ub97c \ucee4\uc2a4\ud130\ub9c8\uc774\uc9d5\ud560 \uc218 \uc788\ub2e4. \uc774 \ub355\ubd84\uc5d0 \ub098\ub294 \uc2f8\uc11c \ubd80\ub2f4\uc5c6\uace0 \ub9cc\ub9cc\ud55c \ub9c8\uc6b0\uc2a4\ub97c \uc0ac\ub2e4\uac00, 7\ub9cc\uc6d0\uc740 \ub4e4\uc5c8\ub358 \ub85c\uc9c0\ud14d \ub9c8\uc6b0\uc2a4, 10\ub9cc\uc6d0 \ub118\uac8c \uc900 \ud2b8\ub799\ud328\ub4dc\uc640 \ub3d9\uc77c\ud55c \uc0ac\uc6a9 \ud658\uacbd\uc744 \ub9cc\ub4e4 \uc218 \uc788\uc5c8\ub2e4. \\n\\n\\n## 2\ub9cc\uc6d0\ub300 \ub9c8\uc6b0\uc2a4\ub85c \ub9e4\uc9c1 \ub9c8\uc6b0\uc2a4/\ud2b8\ub799\ud328\ub4dc \ub300\uccb4\ud558\uae30\\n\\n\\n### 1. \uc900\ube44\ubb3c : \ub0a8\ub294 \ubc84\ud2bc\uc774 3\uac1c \uc788\ub294 \ub9c8\uc6b0\uc2a4\\n\\n<center-image>![\ub85c\uc9c0\ud14d G102 \uc2dc\ub9ac\uc988](./logitech-g.png)</center-image>\\n<full-caption>\uc9c0\uae08 \uc798 \uc4f0\uace0 \uc788\ub294 [\ub85c\uc9c0\ud14d G102 \uc2dc\ub9ac\uc988](https://www.logitechg.com/ko-kr/products/gaming-mice/g102-prodigy-gaming-mouse.910-004849.html)\\n\ucfe0\ud321\uc5d0\uc11c 2\ub9cc\uc6d0 \ucd08\ubc18\ub300\ub77c\uc11c \uc124\uc0ac \ub610 \ub9dd\uac00\uc838\ub3c4(?) \ube44\uad50\uc801 \uc0c1\uad00\uc5c6\ub2e4\ub294 \ub9c8\uc74c\uc73c\ub85c \ud3b8\ud558\uac8c \uc4f8 \uc218 \uc788\ub2e4.</full-caption>\\n\\nMission Control, Desktop Left, Desktop Right\ub97c \ud0a4 \ub9f5\ud551\ud560 \uac83\uc774\uae30 \ub54c\ubb38\uc5d0 \ucd5c\uc18c 3\uac1c\uc758 \ubc84\ud2bc\uc774 \ud544\uc694\ud558\ub2e4. \uadf8\ub798\uc11c \ubcf4\ud1b5 \ube0c\ub77c\uc6b0\uc800 \uc774\uc804/\ub2e4\uc74c \uc6a9\ub3c4\ub85c \uc4f0\uc774\ub294 \ubcf4\uc870 \ud0a4\uac00 \ub2ec\ub824\uc788\uace0, \ub9c8\uc6b0\uc2a4 \ud720\uc774 \ubc84\ud2bc\uc73c\ub85c\ub3c4 \uc4f0\uc77c \uc218 \uc788\ub3c4\ub85d \ub20c\ub9ac\ub294 \ub9c8\uc6b0\uc2a4\uac00 \ud544\uc694\ud558\ub2e4. \ubb3c\ub860, \ud0a4 3\uac1c\ub9cc \ud655\ubcf4\ub418\uba74 \ub418\ubbc0\ub85c \ub9c8\uc6b0\uc2a4 \ud720 \uc678\uc5d0 dpi \ubc84\ud2bc\uc744 \ud65c\uc6a9\ud558\ub294 \uac83\ub3c4 \ubc29\ubc95\uc774\ub2e4.  \\n\\n\ub098\ub294 \ub9c8\uc6b0\uc2a4 \ud720\uc744 mission control, \ub9c8\uc6b0\uc2a4 \uc67c\ucabd \ubcf4\uc870\ud0a4\uc758 \uc88c/\uc6b0\ub97c \uac01\uac01 Desktop Left, Desktop Right\uc5d0 \ub9f5\ud551\ud588\ub2e4.\\n\\n\\n### 2. Karabiner-elements \uc124\uce58\ud558\uace0 \ub9c8\uc6b0\uc2a4 \uc81c\uc5b4 \uad8c\ud55c \uc8fc\uae30\\n\\n\uadf8\ub9ac\uace0 \ub2f9\uc5f0\ud788 Mac\uc5d0 [**Karabiner-elements**](https://karabiner-elements.pqrs.org/)\uac00 \uc124\uce58\ub418\uc5b4 \uc788\uc5b4\uc57c \ud55c\ub2e4. \uadf8\ub9ac\uace0 \uc870\uae08 \uc704\uc5d0 \ucea1\ucc98 \ud654\uba74\uc5d0 \ub098\uc628 \uac83\ucc98\ub7fc **Devices > Basic Configuration**\uc5d0\uc11c \ub0b4 \ub9c8\uc6b0\uc2a4 \uc774\ub984 \uc67c\ucabd\uc5d0 \uccb4\ud06c\ubc15\uc2a4\uac00 \uccb4\ud06c\ub418\uc5b4 \uc788\ub294 \uc0c1\ud0dc\uc5ec\uc57c \ud55c\ub2e4. \uc774\ub294 \ub9c8\uc6b0\uc2a4 \ud0a4\ub97c \uc81c\uc5b4\ud560 \uc218 \uc788\ub294 \uad8c\ud55c\uc744 Karabiner-elements\uc5d0\uac8c \ubd80\uc5ec\ud558\ub294 \uacfc\uc815\uc774\ub2e4.\\n\\n\\n### 3. \ub098\ub9cc\uc758 \ud0a4 \ub9f5\ud551 \ucd94\uac00\ud558\uae30\\n\\n<full-image>![\uce74\ub77c\ubc14\uc774\ub108 rule \ucd94\uac00\ud558\uae30](./karabiner-1.png)</full-image>\\n\\n**Complex modifications** \ud0ed\uc5d0 \ub4e4\uc5b4\uac00\uba74 \ub0b4\uac00 \uc9c1\uc811 \uc124\uc815\ud55c \ud0a4 \uc870\ud569 \uaddc\uce59\uc744 \ucd94\uac00\ud560 \uc218 \uc788\ub2e4. \uc67c\ucabd \uc544\ub798\uc5d0 \uc788\ub294 **Add rule** \ubc84\ud2bc\uc744 \ub204\ub974\uba74 \uc544\ub798\uc640 \uac19\uc740 \ud654\uba74\uc744 \ubcfc \uc218 \uc788\ub2e4. \uc5ec\uae30\uc11c \ud654\uba74 \uc0c1\ub2e8\uc5d0 \uc788\ub294 **Import more rules from the internet (open a web browser)** \ubc84\ud2bc\uc744 \ub204\ub974\uba74 Karabiner-elements \ucee4\ubba4\ub2c8\ud2f0\uc5d0 \uc804\uc138\uacc4 \ub2e4\uc591\ud55c \uc0ac\ub78c\ub4e4\uc774 \uc62c\ub824\ub193\uc740 \ub2e4\uc591\ud55c \uaddc\uce59\ub4e4\uc744 \ubcfc \uc218 \uc788\ub2e4.\\n\\n<full-image>![\uce74\ub77c\ubc14\uc774\ub108 rule \ucd94\uac00\ud558\uae30](./karabiner-2.png)</full-image>\\n\\n\uc5ec\uae30\uc11c \uc544\uc26c\uc6b4 \uc810\uc740 import\ud558\ub294 \ubc29\uc2dd\uc774 \uc544\ub2c8\ub77c \uc0c8 \uaddc\uce59\uc744 \uc9c1\uc811 \uc791\uc131\ud574\uc11c \ucd94\uac00\ud558\ub294 \uac83\uc740 \uc704 \ud654\uba74\uc0c1\uc5d0\uc11c \ubd88\uac00\ub2a5\ud558\ub2e4\ub294 \uc810\uc774\ub2e4. \uaddc\uce59\uc774 \uc801\ud600\uc788\ub294 json \ud30c\uc77c\uc5d0 \uc9c1\uc811 \uc811\uadfc\ud574\uc11c, \uaddc\uce59 \ubd80\ubd84\uc744 \ud3b8\uc9d1\ud574\uc57c \ud55c\ub2e4. \uc774 json \ud30c\uc77c\uc740 **/Users/{user name}/.config/karabiner/karabiner.json** \uacbd\ub85c\uc5d0 \uc704\uce58\ud55c\ub2e4.\\n\\n<full-image>![\uce74\ub77c\ubc14\uc774\ub108 rule \ucd94\uac00\ud558\uae30](./karabiner-3.png)</full-image>\\n\\nSpotlight\ub97c \ucf1c\uc11c \uacbd\ub85c\uac12\uc744 \uc9c1\uc811 \uc785\ub825\ud574\uc11c \ud3f4\ub354\ub97c \uc5f4\uac70\ub098, Finder\ub85c \ud3f4\ub354\uc5d0 \uc811\uadfc\ud558\ub294 \ubc29\ubc95\uc774 \uc788\ub2e4. **.config**\ub77c\ub294 \uc228\uaca8\uc9c4 \ud3f4\ub354 \uc548\uc5d0 \uc788\uae30 \ub54c\ubb38\uc5d0 Finder\ub85c \ub4e4\uc5b4\uac00\ub824\uba74 \uc228\uae40 \ud3f4\ub354\uac00 \ubcf4\uc5ec\uc9c0\ub3c4\ub85d \uc124\uc815\uc744 \ubc14\uafd4\uc8fc\uc5b4\uc57c \ud55c\ub2e4. Finder\uc5d0\uc11c \ud2b9\uc815 \ud3f4\ub354\uc5d0 \ub4e4\uc5b4\uac04 \uc0c1\ud0dc\uc5d0\uc11c **Shift + Command + .**\uc744 \ub204\ub974\uba74 \uc228\uae40 \ud3f4\ub354\uac00 \ubcf4\uc774\ub3c4\ub85d on/off\ud560 \uc218 \uc788\ub2e4.\\n\\n\uadf8\ub9ac\uace0 \ud3b8\ud55c editor \ud504\ub85c\uadf8\ub7a8\uc73c\ub85c **karabiner.json**\uc744 \uc5f4\uba74 \uc5ec\ub7ec \uc124\uc815\uac12\ub4e4\uc774 \ub4e4\uc5b4\uc788\ub294 json \ud30c\uc77c\uc744 \ubcfc \uc218 \uc788\uc744 \uac83\uc774\ub2e4. \uc5ec\uae30\uc11c \uc544\ub798\uc640 \uac19\uc740 \ucf54\ub4dc \ubd80\ubd84\uc744 \ucc3e\uc544\uc57c \ud55c\ub2e4.\\n\\n```json showLineNumbers\\n\\"rules\\" : [\\n\\t{\\"description\\" : \\"my first rule description\\",\\n\\t\\"manipulators\\" : [...]},\\n\\t{\\"description\\" : \\"my second rule description\\",\\n\\t\\"manipulators\\" : [...]},\\n...\\n]\\n```\\n\\nkarabiner-elements\ub97c \ucc98\uc74c \uc4f4\ub2e4\uba74 \ucd94\uac00\ub41c rule\uc774 \uc544\ubb34\uac83\ub3c4 \uc5c6\uc73c\ubbc0\ub85c **rules**\uc758 \uac12\uc774 \ube44\uc5b4\uc788\uc744 \uac83\uc774\ub2e4. \uc774\uc81c \uc774 \uacf3\uc5d0 \ub098\ub9cc\uc758 rule\uc744 \uc544\ub798\uc640 \uac19\uc774 \ucd94\uac00\ud574\uc904 \uac83\uc774\ub2e4.\\n\\n```json showLineNumbers\\n{\\n\\t\\"description\\": \\"mouse wheel to mission_control\\",\\n\\t\\"manipulators\\": [{\\n\\t\\t\\t\\"from\\": {\\n\\t\\t\\t\\t\\t\\"pointing_button\\": \\"button3\\"},\\n\\t\\t\\t\\"to\\": [{\\n\\t\\t\\t\\t\\t\\"key_code\\": \\"mission_control\\"}],\\n\\t\\t\\t\\"type\\": \\"basic\\"}]},\\n{\\n\\t\\"description\\": \\"button4/button5 to Desktop Navigation\\",\\n\\t\\"manipulators\\": [{\\n\\t\\t\\t\\"from\\": {\\n\\t\\t\\t\\t\\t\\"pointing_button\\": \\"button4\\"},\\n\\t\\t\\t\\"to\\": [{\\n\\t\\t\\t\\t\\t\\"key_code\\": \\"left_arrow\\",\\n\\t\\t\\t\\t\\t\\"modifiers\\": [\\"left_control\\"]}],\\n\\t\\t\\t\\"type\\": \\"basic\\"},\\n\\t\\t\\t{\\n\\t\\t\\t\\"from\\": {\\n\\t\\t\\t\\t\\t\\"pointing_button\\": \\"button5\\"},\\n\\t\\t\\t\\"to\\": [{\\n\\t\\t\\t\\t\\t\\"key_code\\": \\"right_arrow\\",\\n\\t\\t\\t\\t\\t\\"modifiers\\": [\\"left_control\\"]}],\\n\\t\\t\\t\\"type\\": \\"basic\\"}]}\\n```\\n\\n\uc5ec\uae30\uc11c **description**\uc740 Karabiner-elements \ud504\ub85c\uadf8\ub7a8\uc0c1\uc5d0\uc11c \ubcf4\uc774\ub294 rule\uc758 \uc124\uba85\uc744 \uc801\ub294 \uacf3\uc774\ub2e4. \uadf8\ub9ac\uace0 **manipulators**\uc548\uc5d0 \uc5b4\ub5a4 \ud0a4\ub97c \uc5b4\ub5a4 \ub3d9\uc791\uc5d0 \ub9f5\ud551\ud560 \uac83\uc778\uc9c0\ub97c \uc815\uc758\ud55c\ub2e4.\\n\\nKarabiner-elements\ub294 \ub9c8\uc6b0\uc2a4 \uc88c\uc6b0 \ud074\ub9ad \ubc84\ud2bc\uc744 \uac01\uac01 button1, 2\ub85c, \ub9c8\uc6b0\uc2a4 \ud720 \ubc84\ud2bc\uc744 button3\ub85c \uc778\uc2dd\ud55c\ub2e4. \uadf8\ub9ac\uace0 \ubcf4\ud1b5 \ub9c8\uc6b0\uc2a4 \uc67c\ucabd\uc5d0 \uc788\ub294 \uc88c/\uc6b0 \ubc84\ud2bc\uc744 \uac01\uac01 button4, button5\ub85c \uc778\uc2dd\ud558\uae30 \ub54c\ubb38\uc5d0 \uac01 \ubc84\ud2bc \ubc88\ud638\uc5d0 \ub9de\ucdb0\uc11c \uc81c\uc2a4\ucc98\ub97c \ub9f5\ud551\ud588\ub2e4.\\n\\n\ub2e4\ub978 \ub9c8\uc6b0\uc2a4 \ubc84\ud2bc\uc5d0 \ub9f5\ud551\ud558\uace0 \uc2f6\uc740 \uacbd\uc6b0 \uc784\uc2dc\ub85c button1, 2, 3 \ub4f1\uc5d0\ub2e4\uac00 a, b, c \uac19\uc740 \uc54c\ud30c\ubcb3\uc774 \ub20c\ub9ac\ub3c4\ub85d \uc124\uc815\ud55c \ud6c4, \uc9c1\uc811 \ub20c\ub7ec\ubcf4\uba74 \ub41c\ub2e4. \uc774 \ub54c \uc785\ub825\ub418\ub294 \uc54c\ud30c\ubcb3\uc744 \ubcf4\uace0 \ubc84\ud2bc\uc758 \ubc88\ud638\ub97c \uc54c\uc544\ub0bc \uc218 \uc788\ub2e4. \uadf8 \ud6c4\uc5d0 \uc704 json \uc791\uc131\ubc95\uc5d0\uc11c pointing_button\uc5d0 \ubc84\ud2bc \ubc88\ud638\ub97c \ub123\uc5b4\uc8fc\uba74 \ub41c\ub2e4. 1\uac1c\uc758 \ud0a4\ub9cc \ub9f5\ud551\ud55c\ub2e4\uba74 **\\"key_code\\" : {key name}**\uc73c\ub85c \ub05d\ub0b4\uba74 \ub418\uc9c0\ub9cc, \ud55c \ubc88\uc5d0 \uc5ec\ub7ec \ubc84\ud2bc\uc744 \uc870\ud569\ud558\ub294 \uacbd\uc6b0\ub77c\uba74 **key_code : {key name}, \\"modifiers\\" : [\\"key name\\", \\"key name\\"]**\uc640 \uac19\uc774 \uc785\ub825\ud558\uba74 \ub41c\ub2e4.\\n\\n\\n## \ub9c8\uce58\uba70\\n\\nKarabiner-elements\ub294 \ub0b4\uac00 \uad6c\ud604\ud558\uace0 \uc2f6\uc740 \uc628\uac16 \ud0a4 \ub9f5\ud551\uc744 \uc790\uc720\uc790\uc7ac\ub85c \ud560 \uc218 \uc788\ub2e4\ub294 \uc810\uc5d0\uc11c \uc544\uc8fc \uac15\ub825\ud55c \ud234\uc774\ub2e4. [\ubcf4\uc870 \ubc84\ud2bc\uc774 \ubb34\uc2a8 SF \uc601\ud654\uc5d0 \ub4f1\uc7a5\ud560 \uac83 \uac19\uc740 \ub514\uc790\uc778\uc73c\ub85c \ub9ce\uc740 \ub9c8\uc6b0\uc2a4](https://cdmanii.com/5119)\ub3c4 \ubd24\uc5c8\ub294\ub370\u2026 \uc804\ubb38 \uc791\uc5c5\uc744 \uc624\ub798 \ud558\ub294 \uc0ac\ub78c\uc774\ub77c\uba74 \ud504\ub85c\uadf8\ub7a8\ubcc4 \ub2e8\ucd95\ud0a4\ub97c \ub9f5\ud551\ud574\ub193\ub294 \uac83\ub3c4 \uc3e0\uc3e0\ud55c \uc0dd\uc0b0\uc131 \uc99d\uc9c4\uc774\uc9c0 \uc54a\uc744\uae4c \uc2f6\ub2e4."}]}')}}]);